// client/src/components/OCRTest.js

import React, { useState } from 'react';
import Tesseract from 'tesseract.js';

const OCRTest = () => {
  const [text, setText] = useState('');
  const [image, setImage] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));
    }
  };

  const handleImageUpload = () => {
    if (image) {
      Tesseract.recognize(
        image,
        'eng',
        {
          logger: info => console.log(info) // Log progress for debugging
        }
      ).then(({ data: { text } }) => {
        setText(text);
      });
    }
  };

  return (
    <div>
      <h2>OCR Test</h2>
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleImageUpload}>Process Image</button>
      <div>
        <img src={image} alt="Selected" style={{ maxWidth: '100%', marginTop: '20px' }} />
        <pre>{text}</pre>
      </div>
    </div>
  );
};

export default OCRTest;
